import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import whyShouldYouJoinUs from '../../images/Internships/WhyShouldYouJoinUs.jpg';


export default function WhyShouldYouJoinUs(){
    return(
        <div>
                <Container style={{ color: "#a00", fontSize: "50px", fontWeight: "bold", padding: "30px" }}>
                    <Row>
                        <Col xs={12} md={6} lg={6}>
                        <img src={whyShouldYouJoinUs} />
                        </Col>
                    </Row>
                </Container>
            </div>
    )
}
